<script>    $(document).ready(function () {        $("#submitButton").click();        $("#second_menu_list").find("#li_admin_settings").addClass("active");        $("#btn_update_password").on("click", function () {            form = $("#form_update_password");            url = base + "admin_settings/update_password";            data = form.serialize();            $.post({                url: url,                data: data            }).done(function (response) {                if (IsJsonString(response)) {                    data = JSON.parse(response);                    if (data == true) {                        success("Password Updated Successfully");                        $(form)[0].reset();                    } else {                        error(data);                    }                } else {                    error("Unexpected Error Occured");                }            });        });    });</script>