<script>    function set_row4(id, row) {        $(row).attr("data-id", id);        $(row).addClass("admin_triage_row");    }    function set_missing_status(status) {        if (status == "Missing item requested") {            return '<span class="fc-event-dot" style="background-color:#f74444"></span>  ' + status;        } else if (status == "Items uploaded for review") {            return '<span class="fc-event-dot" style="background-color:#88b794"></span>  ' + status;        } else {            return status;        }    }    $(document).ready(function () {        $("#li_admin_triage").addClass("active");        $('table#table_admin_triage').on("click", ".admin_triage_row", function () {            let id = $(this).data('id');            location.href = base + "admin_triage/referral_details/" + id;        });        //  *** Admin Triage Datatable        global_data.table_admin_triage_title = "Admin Triage Referral";        global_data.table_admin_triage = $("#table_admin_triage").DataTable({            "processing": true,            "serverSide": true,            "autoWidth": false,            "language": {                "emptyTable": "Admin triage is empty",                "info": "Showing _START_ to _END_ of _TOTAL_ " + global_data.table_admin_triage_title,                "infoEmpty": "No results found",                "infoFiltered": "(filtered from _MAX_ total " + global_data.table_admin_triage_title + ")",                "infoPostFix": "",                "thousands": ",",                "lengthMenu": "Show _MENU_ ",                "loadingRecords": "Loading " + global_data.table_admin_triage_title,                "processing": "Processing " + global_data.table_admin_triage_title,                "search": "",                "zeroRecords": "No matching " + global_data.table_admin_triage_title + " found"            },            "ajax": "<?php echo base_url(); ?>admin_triage/ssp_admin_triage",            "rowCallback": function (row, data, index) {                $('td:eq(4)', row).html(                        set_row4(data[4], row)                        );                $('td:eq(3)', row).html(                        set_missing_status(data[3])                        );                $(row).addClass('db-table-link-row');            },            "dom": get_dom_plan(),            // "drawCallback": set_patients_table,            "columnDefs": [                {"width": "25%", "targets": 0},                {"width": "25%", "targets": 1},                {"width": "25%", "targets": 2},                {"width": "25%", "targets": 3}            ]        });        $("#table_admin_triage").wrap('<div class="table-responsive"></div>');        // $("#btn_view_add_patient").appendTo("#patients_table_wrapper .button-placeholder");        $(".dataTables_filter input").before('<i class="fa fa-search datatable-search"></i>');        $(".dataTables_filter input").attr('placeholder', 'Search');        //  *** Admin Triage Datatable Over    });</script> 