<script>    console.log("whole");    function set_completed_table_row(id, status, row) {        $(row).attr("data-id", id);        $(row).attr("data-status", status.toLowerCase());        $(row).addClass("completed_row");    }    $(document).ready(function () {        $("#li_all_records").addClass("active");        //  *** Completed Datatable        global_data.table_completed_title = "Patient Records";        global_data.table_completed = $("#table_completed").DataTable({            "processing": true,            "serverSide": true,            "autoWidth": false,            "language": {                "emptyTable": "There are no " + global_data.table_completed_title,                "info": "Showing _START_ to _END_ of _TOTAL_ " + global_data.table_completed_title,                "infoEmpty": "No results found",                "infoFiltered": "(filtered from _MAX_ total " + global_data.table_completed_title + ")",                "infoPostFix": "",                "thousands": ",",                "lengthMenu": "Show _MENU_ ",                "loadingRecords": "Loading " + global_data.table_completed_title,                "processing": "Processing " + global_data.table_completed_title,                "search": "",                "zeroRecords": "No matching " + global_data.table_completed_title + " found"            },            "ajax": "<?php echo base_url(); ?>all_records/ssp_all_records",            "rowCallback": function (row, data, index) {                $('td:eq(5)', row).html(                        set_completed_table_row(data[5], data[4], row)                        );                $(row).addClass('db-table-link-row');            },            "dom": get_dom_plan(),            // "drawCallback": set_patients_table,            "columnDefs": [                {"width": "33%", "targets": 0},                {"width": "33%", "targets": 1},                {"width": "34%", "targets": 2}            ]        });        $("#table_completed").wrap('<div class="table-responsive"></div>');        // $("#btn_view_add_patient").appendTo("#patients_table_wrapper .button-placeholder");        $(".dataTables_filter input").before('<i class="fa fa-search datatable-search"></i>');        $(".dataTables_filter input").attr('placeholder', 'Search');        $('table#table_completed').on("click", ".completed_row", function () {            console.log("row completed_row");            let id = $(this).data('id');            let status = $(this).data('status');            location.href = base + status + "/referral_details/" + id;        });        //  *** Completed Datatable Over    });</script> 