<script>    function set_td_status(status) {        let template = "";        if (status === "completed") {            template = $("#templates").find("#template_td_status_completed").html();        } else {            template = $("#templates").find("#template_td_status_awaiting").html();        }        return template;    }    function set_td_action(id, row) {        $(row).attr("data-id", id);        $(row).addClass("referral_triage_row");        return $("#templates").find("#template_td_action").html();     }    $(document).ready(function () {        $("#li_call_center").addClass("active");//        $('table#table_call_center').on("click", ".referral_triage_row", function () {//            let id = $(this).data('id');//            location.href = base + "referral_triage/referral_details/" + id;//        });        //  *** Physician Triage Datatable        global_data.table_call_center_title = "Call Center";        global_data.table_call_center = $("#table_call_center").DataTable({            "processing": true,            "serverSide": true,            "order": [],            "autoWidth": false,            "pageLength": 50,            "language": {                "emptyTable": "Call center is empty",                "info": "Showing _START_ to _END_ of _TOTAL_ " + global_data.table_call_center_title,                "infoEmpty": "No results found",                "infoFiltered": "(filtered from _MAX_ total " + global_data.table_call_center_title + ")",                "infoPostFix": "",                "thousands": ",",                "lengthMenu": "Show _MENU_ ",                "loadingRecords": "Loading " + global_data.table_call_center_title,                "processing": "Processing " + global_data.table_call_center_title,                "search": "",                "zeroRecords": "No matching " + global_data.table_call_center_title + " found"            },            "ajax": "<?php echo base_url(); ?>call_center/ssp_call_center",            "rowCallback": function (row, data, index) {                $('td:eq(4)', row).html(                        set_td_status(data[4], row)                        );                $('td:eq(5)', row).html(                        set_td_action(data[5])                        );            },            "dom": get_dom_plan()                    // "drawCallback": set_patients_table,//            "columnDefs": [//                {"width": "25%", "targets": 0},//                {"width": "25%", "targets": 1},//                {"width": "25%", "targets": 2},//                {"width": "25%", "targets": 3}//            ]        });        $("#table_call_center").wrap('<div class="table-responsive"></div>');        // $("#btn_view_add_patient").appendTo("#patients_table_wrapper .button-placeholder");        $(".dataTables_filter input").before('<i class="fa fa-search datatable-search"></i>');        $(".dataTables_filter input").attr('placeholder', 'Search');        //  *** Referral Triage Datatable Over    });</script> 