<script>    function set_row_id(id, status, row) {        status = status.toLowerCase();        if (status == "cancelled") {            status = "completed";        }        $(row).attr("data-id", id);        $(row).attr("data-status", status);        $(row).addClass("completed_row");    }    $(document).ready(function () {        $("#submitButton").click();        $("#li_all_patient_records").addClass("active");        $('table#table_completed').on("click", ".completed_row", function () {            let id = $(this).data('id');            let status = $(this).data('status');            if (status == "scheduled" || status == "completed") {                location.href = base + "scheduled" + "/referral_details/" + id;            } else {                location.href = base + "completed" + "/referral_details/" + id;            }        });        //  *** Completed Datatable        global_data.table_completed_title = "Completed Referral";        global_data.table_completed = $("#table_completed").DataTable({            "processing": true,            "serverSide": true,            "autoWidth": false,            "language": {                "emptyTable": "There are no completed referrals",                "info": "Showing _START_ to _END_ of _TOTAL_ " + global_data.table_completed_title,                "infoEmpty": "No results found",                "infoFiltered": "(filtered from _MAX_ total " + global_data.table_completed_title + ")",                "infoPostFix": "",                "thousands": ",",                "lengthMenu": "Show _MENU_ ",                "loadingRecords": "Loading " + global_data.table_completed_title,                "processing": "Processing " + global_data.table_completed_title,                "search": "",                "zeroRecords": "No matching " + global_data.table_completed_title + " found"            },            "ajax": "<?php echo base_url(); ?>completed/ssp_completed",            "rowCallback": function (row, data, index) {                $('td:eq(5)', row).html(                        set_row_id(data[5], data[4], row)                        );                $(row).addClass('db-table-link-row');            },            "dom": get_dom_plan(),            // "drawCallback": set_patients_table,            "columnDefs": [                {"width": "20%", "targets": 0},                {"width": "20%", "targets": 1},                {"width": "20%", "targets": 2},                {"width": "20%", "targets": 3},                {"width": "20%", "targets": 4},            ]        });        $("#table_completed").wrap('<div class="table-responsive"></div>');        // $("#btn_view_add_patient").appendTo("#patients_table_wrapper .button-placeholder");        $(".dataTables_filter input").before('<i class="fa fa-search datatable-search"></i>');        $(".dataTables_filter input").attr('placeholder', 'Search');        //  *** Completed Datatable Over    });</script> 