<script>    function set_row(id, row) {        $(row).attr("id", id);    }    $(document).ready(function () {        $("#submitButton").click();        $("#second_menu_list").find("#li_manage_physician").addClass("active");        $("#btn_view_add_physician").on("click", function () {            $("#form_add_physician")[0].reset();            $("#modal_add_physician").modal("show");        });        $("#btn_add_physician").on("click", function () {            url = base + "manage_physician/add_physician";            data = $("#form_add_physician").serialize();            $.post({                url: url,                data: data            }).done(function (response) {                console.log(response);                if (IsJsonString(response)) {                    data = JSON.parse(response);                    if (data === true) {                        $(".modal").modal("hide");                        success("Physician Added Successfully");                        global_data.table_physicians.ajax.reload();                    } else {                        error(data);                    }                }            });        });        //  *** Inbox Datatable        global_data.table_physicians_title = "Clinic Physicians";        global_data.table_physicians = $("#table_physicians").DataTable({            "processing": true,            "serverSide": true,            "autoWidth": false,            "language": {                "emptyTable": "No physicians have been added",                "info": "Showing _START_ to _END_ of _TOTAL_ " + global_data.table_physicians_title,                "infoEmpty": "No results found",                "infoFiltered": "(filtered from _MAX_ total " + global_data.table_physicians_title + ")",                "infoPostFix": "",                "thousands": ",",                "lengthMenu": "Show _MENU_ ",                "loadingRecords": "Loading " + global_data.table_physicians_title,                "processing": "Processing " + global_data.table_physicians_title,                "search": "",                "zeroRecords": "No matching " + global_data.table_physicians_title + " found"            },            "ajax": "<?php echo base_url(); ?>manage_physician/ssp_clinic_physician",            "rowCallback": function (row, data, index) {                $('td:eq(4)', row).html(                        set_row(data[4], row)                        );                $(row).addClass('db-table-link-row');            },            "dom": get_dom_plan(),            // "drawCallback": set_patients_table,            "columnDefs": [                {"width": "30%", "targets": 0},                {"width": "30%", "targets": 1},                {"width": "20%", "targets": 2},                {"width": "20%", "targets": 3}            ]        });        $("#table_physicians").wrap('<div class="table-responsive"></div>');        // $("#btn_view_add_patient").appendTo("#patients_table_wrapper .button-placeholder");        $("#table_physicians_wrapper .dataTables_filter input").before('<i class="fa fa-search datatable-search"></i>');        $("#table_physicians_wrapper .dataTables_filter input").attr('placeholder', 'Search');        //  *** Inbox Datatable Over    });</script>