<script>    function set_row4(id, row) {        $(row).attr("data-id", id);        $(row).addClass("referral_triage_row");    }    function set_missing_status(status) {        if (status === "Missing item requested") {            return '<span class="fc-event-dot" style="background-color:#f74444"></span>  ' + status;        } else if (status === "Items uploaded for review") {            return '<span class="fc-event-dot" style="background-color:#88b794"></span>  ' + status;        } else {            return status;        }    }    function get_clinic_physicians() {        form = $("#sample_form");        url = base + "referral/get_clinic_physicians";        data = form.serialize();        $.post({            url: url,            data: data        }).done(function (response) {            if (IsJsonString(response)) {                data = JSON.parse(response);                options = "<option value='unassign'>Unassigned</option>";                for (i = 0; i < data.length; i++) {                    options += "<option value='" + data[i].id + "'>" + data[i].physician_name + "</option>";                }                $("#assigned_physician").html(options);            }        });    }    //for reason referral    var x_reason = 0; //initial text box count    function add_reason(text) {        if (x_reason < 1) { //max input box allowed            x_reason++; //text box increment            wrapper = $("#btn_add_reason").closest("div.wrapper_div").find(".edit_reasons");            $(wrapper).append('<div><div class="checkbox"><label><input type="checkbox" name="reasons[]" value="' + text + '" checked><span class="cr"><i class="cr-icon fa fa-check"></i><i class="bully" style="display: none;">-</i></span><input type="text" placeholder="Type your text" value="' + text + '" class="dummy_checkbox"/></label><a href="#" class="remove_field edit_reason">&nbsp;&nbsp;<i class="fa fa-minus-circle" aria-hidden="true"></i></a></div></div>'); //add input box        }    }    $(document).ready(function () {        get_clinic_physicians();        $("#btn_view_add_new_patient").on("click", function () {            $("#modal_add_new_patient").modal("show");        });        $("#form_add_patient").on("click", "#btn_add_reason", function (e) {            //on add input button click            add_reason("");        });        $("#form_add_patient").on("click", ".edit_reason.remove_field", function (e) {            //user click on remove text            $(this).closest("div.checkbox").remove();            x_reason--;        });        $("#btn_add_new_patient").on("click", function () {            form = $("#form_add_patient");            data = $(form).serialize();            url = base + "referal_triage/new_referral";            $.post({                url: url,                data: data            }).done(function (response) {                if (IsJsonString(response)) {                    data = JSON.parse(response);                    if (data == true) {                        $(".modal").modal("hide");                        success("Patient Successfully Added");                        global_data.table_accepted.ajax.reload();                        get_latest_dashboard_counts();                        $("#form_add_patient")[0].reset();                    } else {                        error(data);                    }                } else {                    error("Unexpected Error Occured");                }            });        });        $.dobPicker({            daySelector: '#form_add_patient #pat_dob_day', /* Required */            monthSelector: '#form_add_patient #pat_dob_month', /* Required */            yearSelector: '#form_add_patient #pat_dob_year', /* Required */            dayDefault: 'Day', /* Optional */            monthDefault: 'Month', /* Optional */            yearDefault: 'Year', /* Optional */            minimumAge: 0, /* Optional */            maximumAge: 120 /* Optional */        });        $("#li_referral_triage").addClass("active");        $('table#table_referral_triage').on("click", ".referral_triage_row", function () {            let id = $(this).data('id');            location.href = base + "referral_triage/referral_details/" + id;        });        //  *** Physician Triage Datatable        global_data.table_referral_triage_title = "Referral Triage Referral";        global_data.table_referral_triage = $("#table_referral_triage").DataTable({            "processing": true,            "serverSide": true,            "order": [],            "autoWidth": false,            "pageLength": 50,            "language": {                "emptyTable": "Referral triage is empty",                "info": "Showing _START_ to _END_ of _TOTAL_ " + global_data.table_referral_triage_title,                "infoEmpty": "No results found",                "infoFiltered": "(filtered from _MAX_ total " + global_data.table_referral_triage_title + ")",                "infoPostFix": "",                "thousands": ",",                "lengthMenu": "Show _MENU_ ",                "loadingRecords": "Loading " + global_data.table_referral_triage_title,                "processing": "Processing " + global_data.table_referral_triage_title,                "search": "",                "zeroRecords": "No matching " + global_data.table_referral_triage_title + " found"            },            "ajax": "<?php echo base_url(); ?>referral_triage/ssp_referral_triage",            "rowCallback": function (row, data, index) {                $('td:eq(4)', row).html(                        set_row4(data[4], row)                        );                $('td:eq(3)', row).html(                        set_missing_status(data[3])                        );            },            "dom": get_dom_plan()                    // "drawCallback": set_patients_table,//            "columnDefs": [//                {"width": "25%", "targets": 0},//                {"width": "25%", "targets": 1},//                {"width": "25%", "targets": 2},//                {"width": "25%", "targets": 3}//            ]        });        $("#table_referral_triage").wrap('<div class="table-responsive"></div>');        // $("#btn_view_add_patient").appendTo("#patients_table_wrapper .button-placeholder");        $(".dataTables_filter input").before('<i class="fa fa-search datatable-search"></i>');        $(".dataTables_filter input").attr('placeholder', 'Search');        //  *** Referral Triage Datatable Over    });</script> 